import 'bootstrap/dist/css/bootstrap.min.css';
import "../App.css"

import React, {useRef, useState} from "react"
import {Row, Col, Image, Button, Container} from "react-bootstrap"


export default function AnalysisPage(){

    const fileInputRef = useRef(null);
    const [selectedImage, setSelectedImage] = useState(null);
    const [prediction, setPrediction] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleButtonClick = () =>{
        fileInputRef.current.click();
    };

    const handleFileChange = async (event) => {

        const file = event.target.files[0];
        if (!file) return;

        const imageUrl = URL.createObjectURL(file);
        setSelectedImage(imageUrl);

        const formData = new FormData();
        formData.append("image", file);

        try {
            setLoading(true);
            const res = await fetch("http://127.0.0.1:5000/api/ai-skin-analysis/", {
                method: "POST",
                body: formData
            });

            const data = await res.json();
            console.log(data)
            setPrediction(data.primary_concern);

        } catch (error){
            console.error("Error fetching result", error);

        } finally {
            setLoading(false);
        }
    };

    return(
        <>
        <main>
            <Container>
                {!loading ? (
                    <>
                        <Row>
                            <Col>
                                <div>
                                    <Image src="images/ai-skin-page.png" rounded fluid/>

                                </div>
                            </Col>
                            <Col className="analysis-page-text">
                                <div>
                                    <h1>AI Skin Analysis</h1>
                                    <p></p>
                                    <p>Ready for Your Personalized Skin Journey?</p>
                                    <p>To ensure the most accurate results, please upload a well-lit, clear image of your face.</p>
                                    <ul>
                                        <li>Ensure your face is free of makeup.</li>
                                        <li>Use natural, even lighting if possible.</li>
                                        <li>Look directly at the camera.</li>
                                    </ul>

                                    <Button 
                                        variant="outline-dark"
                                        size="lg"
                                        onClick={handleButtonClick}
                                    >
                                        Start My Analysis
                                    </Button>

                                    <input
                                        type="file"
                                        accept="image/*"
                                        ref={fileInputRef}
                                        onChange={handleFileChange}
                                        style={{display:"none"}}
                                    />
                                </div>
                            </Col>
                        </Row>        
                    </>
                ) : (
                    <>
                        <div>
                            {selectedImage && (
                                <img 
                                    src={selectedImage}
                                    alt="Preview"
                                    className="preview-image"
                                />
                            )}

                            <Spinner />

                            {loading && (
                                <p>Analysing your skin...</p>
                            )}

                            {prediction && (
                                <p>Your primary skin concern: {prediction.toUpperCase()}</p>
                            )}
                        </div>
                    </>
                )}



            </Container>
        </main>
        </>
    )
}
