import 'bootstrap/dist/css/bootstrap.min.css';
import "../App.css"

import {useRef, useState, useEffect} from "react";
import {Row, Col, Image, Button, Container} from "react-bootstrap";


export default function AnalysisPage(){

    const fileInputRef = useRef(null);
    const [selectedImage, setSelectedImage] = useState(null);
    const [prediction, setPrediction] = useState(null);
    const [loading, setLoading] = useState(false);
    const [analysisTaken, setAnalysisTaken] = useState(false);


    const handleButtonClick = () =>{
        fileInputRef.current.click();
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file){
            const imageUrl = URL.createObjectURL(file);
            setSelectedImage(imageUrl);
        }
    };

    useEffect(() => {
        if (selectedImage){
            fetchSkinConcern();
            setAnalysisTaken(true);
        }
    }, [selectedImage]);

    const fetchSkinConcern = async() => {
        try{
            setLoading(true);
            const res = await fetch("http://127.0.0.1:5000/api/skin-analysis/",{
                method: "POST",
                headers: {"Content-Type": ""},
                body: {selectedImage},
            });
            const data = await res.json();
            setPrediction(data.skin_concern);

        } catch(error){
            console.error("Error:", error);
        } finally{
            setLoading(false);
        }    
    };



    return(
        <>
        <main>
            <Container>
                {!analysisTaken ? (
                    <>
                        <Row>
                            <Col>
                                <div>
                                    <Image src="images/ai-skin-page.png" rounded fluid/>

                                </div>
                            </Col>
                            <Col className="analysis-page-text">
                                <div>
                                    <h1>AI Skin Analysis</h1>
                                    <p></p>
                                    <p>Ready for Your Personalized Skin Journey?</p>
                                    <p>To ensure the most accurate results, please upload a well-lit, clear image of your face.</p>
                                    <ul>
                                        <li>Ensure your face is free of makeup.</li>
                                        <li>Use natural, even lighting if possible.</li>
                                        <li>Look directly at the camera.</li>
                                    </ul>
                                    <Button 
                                        variant="outline-dark"
                                        size="lg"
                                        onClick={handleButtonClick}
                                    >
                                        Start My Analysis
                                    </Button>
                                    <input
                                        type="file"
                                        accept="image/*"
                                        ref={fileInputRef}
                                        onChange={handleFileChange}
                                        style={{display:"none"}}
                                    />
                                    {selectedImage && (
                                        <img 
                                            src={selectedImage}
                                            alt="Preview"
                                            className="preview-image"
                                        />
                                    )}
                                </div>
                            </Col>
                        </Row>
                    </>
                ) : (
                    <>
                        {!loading ? (
                            <>
                                <Spinner/>
                                <h3>
                                    Sit back & relax.. <br /> 
                                    while our AI tool fetches your result..
                                </h3>
                            </>
                        ) : (
                            <>
                                {prediction && (
                                    <p>You primary skin concern is : {prediction}</p>
                                )}  
                            </>
                        )}
                    </>
                )}

            </Container>
        </main>
        </>
    )
}
